import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import AddTask from './components/AddTask';
import GetTask from './components/GetTask';

function App() {
  const [task, setTask] = useState([]);
  const url =
    'https://first-demo--tasks-default-rtdb.firebaseio.com/Tasks.json';

  // GET-Request To Get Tasks From The Data Base

  function getTasksHandeler() {
    fetch(url)
      .then((res) => res.json())
      .then((data) => setTask(data));
  }
  // const tasks = [
  //   {
  //     taskTitle: 'HELLO WORLD',
  //     taskRemainder: 'Welcome Home',
  //   },
  // ];
  // const getTasksHandeler = () => {
  //   setTask(tasks);
  // };

  return (
    <>
      <Header />
      <AddTask />
      <button onClick={getTasksHandeler}>Click To console</button>
      <GetTask tasks={task} />
    </>
  );
}

export default App;
