import React, { useRef } from 'react';

const AddTask = () => {
  const taskRef = useRef();
  const remainderRef = useRef();

  const submitHandeler = (e) => {
    e.preventDefault();

    const enteredTask = taskRef.current.value;
    const enteredRemainder = remainderRef.current.value;

    // POST-Request To Add Tasks in The Data-Base
    fetch('https://first-demo--tasks-default-rtdb.firebaseio.com/Tasks', {
      method: 'POST',
      body: JSON.stringify({
        task: enteredTask,
        reminder: enteredRemainder,
      }),
      headers: { 'content-type': 'application/json' },
    }).then((res) => res.json());
  };

  return (
    <section>
      <form onSubmit={submitHandeler}>
        <div>
          <label htmlFor='email'>Your Task</label>
          <input ref={taskRef} placeholder='Type Your Task Here' />
        </div>
        <div>
          <label>Your remainder</label>
          <input ref={remainderRef} placeholder='Enter Your Remainder' />
        </div>
        <div>
          <button>Add Task</button>
        </div>
      </form>
    </section>
  );
};

export default AddTask;
